import React, { useState } from 'react';
import { SketchPicker } from 'react-color';

function CreatorPaleta() {
    const [culori, setCulori] = useState([]);
    const [culoareSelectata, setCuloareSelectata] = useState('#ffffff');

    const schimbaCuloare = (culoare) => {
        setCuloareSelectata(culoare.hex);
    };

    const adaugaCuloare = () => {
        if (!culori.includes(culoareSelectata)) {
            setCulori([...culori, culoareSelectata]);
        }
    };

    const eliminaCuloare = (culoareDeEliminat) => {
        setCulori(culori.filter(culoare => culoare !== culoareDeEliminat));
    };

    return (
        <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
            <SketchPicker color={culoareSelectata} onChangeComplete={schimbaCuloare} className='h-128'/>
            <button className="mt-4 bg-blue-500 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" onClick={adaugaCuloare}>
                AdaugÄƒ Culoare
            </button>
            <div className="flex flex-wrap justify-center mt-4">
                {culori.map((culoare, index) => (
                    <div key={index} className="m-2 w-16 h-16 flex justify-center items-center" style={{ backgroundColor: culoare }}>
                        <button className="text-white text-sm" onClick={() => eliminaCuloare(culoare)}>X</button>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default CreatorPaleta;
