import React, { useState } from 'react';

function RoataMonocromatica() {
    const [selectedSegment, setSelectedSegment] = useState(null);

    // Folosim 12 culori diferite pentru roată
    const colors = [
        '#FF0000', '#FFA500', '#FFFF00', '#808000',
        '#008000', '#008080', '#00FFFF', '#0000FF',
        '#800080', '#FF00FF', '#800000', '#000080'
    ];

    // Coordonate pentru fiecare segment bazate pe index
    const calculatePath = (index) => {
        const startAngle = (index * 30) * (Math.PI / 180);
        const endAngle = ((index + 1) * 30) * (Math.PI / 180);
        const largeArc = (endAngle - startAngle) % (Math.PI * 2) > Math.PI ? 1 : 0;
        const startX = 18 + 16 * Math.cos(startAngle);
        const startY = 18 + 16 * Math.sin(startAngle);
        const endX = 18 + 16 * Math.cos(endAngle);
        const endY = 18 + 16 * Math.sin(endAngle);

        return `M18 18 L ${startX} ${startY} A 16 16 0 ${largeArc} 1 ${endX} ${endY} Z`;
    };

    return (
        <div className="container mx-auto px-4 py-6 text-center">
            <h1 className="text-2xl font-bold mb-4">Roata Monocromatică</h1>
            <svg viewBox="0 0 36 36" className="w-64 h-64 mx-auto">
                {colors.map((color, index) => (
                    <path
                        key={index}
                        d={calculatePath(index)}
                        fill={selectedSegment === index ? color : '#D3D3D3'}
                        stroke="white"
                        strokeWidth="0.5"
                        onClick={() => setSelectedSegment(index)}
                    />
                ))}
            </svg>
            <p className="mt-4">Selectați un segment pentru a vizualiza culoarea sa, iar restul segmentelor vor deveni gri.</p>
        </div>
    );
}

export default RoataMonocromatica;
