import React, { useState, useEffect } from 'react';
import Effect from './efect';
import './joc.css';


function Joc() {
  const [isOverVisible, setIsOverVisible] = useState(false);
  const [score, setScore] = useState(5);
  const [correctCounter, setCorrectCounter] = useState(0);
  const [numSquares, setNumSquares] = useState(4);
  const [colors, setColors] = useState([]);
  const [pickedColor, setPickedColor] = useState("");
  const [bestScore, setBestScore] = useState(0);
  const [difficulty, setDifficulty] = useState(4);

  const [isMenuVisible, setIsMenuVisible] = useState(false);

  const toggleMenu = () => {
    setIsMenuVisible(!isMenuVisible);
  };

  useEffect(() => {
    reset();
  }, [numSquares]); 

  useEffect(() => {
   
    resetGame();
  }, [numSquares]);

  const genRandomColors = (num) => {
    // Generează un array de culori aleatorii
    let arr = [];
    for (let i = 0; i < num; i++) {
      arr.push(randomColor());
    }
    return arr;
  };

  const randomColor = () => {
    // Generează o singură culoare aleatorie
    const r = Math.floor(Math.random() * 256);
    const g = Math.floor(Math.random() * 256);
    const b = Math.floor(Math.random() * 256);
    return `rgb(${r}, ${g}, ${b})`;
  };

  

  var handleSquareClick = (color,index) => {
    if (color === pickedColor) {
      setCorrectCounter((prevCorrectCounter) => {
        const updatedCorrectCounter = prevCorrectCounter + 1;
        setBestScore((prevBestScore) => Math.max(prevBestScore, updatedCorrectCounter));
        return updatedCorrectCounter; 
      });

      reset(); // Sau reset(), asigură-te că folosești consistent funcția dorită pentru resetare
    } else {
      setScore(score-1);
      setColors(prevColors => prevColors.map((item, idx) => 
      idx === index ? { ...item, color: 'grey', isClicked: true } : item
    ));
      if(score===1){
        resetGame();
        setIsOverVisible(true)
      }
    }
  };
  
  
  var resetGame = () => {
    var newColors = genRandomColors(numSquares);
    var newPickedColor = newColors[Math.floor(Math.random() * newColors.length)];
    setColors(newColors);
    setPickedColor(newPickedColor);
    setIsOverVisible(false);
    setScore(5);
    setCorrectCounter(0);
  };


  var reset = () => {
    var newColors = genRandomColors(numSquares);
    var newPickedColor = chooseColor(newColors);
    setColors(newColors);
    setPickedColor(newPickedColor);

  };

  var chooseColor = (colors) => {
    var random = Math.floor(Math.random() * colors.length);
    return colors[random];
  };


  var changeDifficulty = (newDifficulty) => {
    setNumSquares(newDifficulty);
    setDifficulty(newDifficulty); // Actualizează starea difficulty la noua dificultate selectată
    reset();
  };


  return (
    <>
   
      {isOverVisible && (
        <div id="over">
          <h1 id="finalScoreF">Final Score: {correctCounter}</h1>
          <h1 id="finalBestScoreF">Best Score: {bestScore}</h1>
          <button id="playAgain" onClick={() => setIsOverVisible(false)}>Play Again</button>
        </div>
      )}

     
      <div id="stripe0" className='w-full flex'>
   
      <Effect className="w-2/5" />
    
      <div className='w-3/5 flex justify-between '>
        <button className={`mode bg-gray-600 mx-4 ${difficulty === 2 ? 'selected' : ''}`} onClick={() => changeDifficulty(2)}>Usor</button>
        <button className={`mode bg-gray-600 mx-4 ${difficulty === 4 ? 'selected' : ''}`} onClick={() => changeDifficulty(4)}>Normal</button>
        <button className={`mode bg-gray-600 mx-4 ${difficulty === 6 ? 'selected' : ''}`} onClick={() => changeDifficulty(6)}>Greu</button>
        </div>
      </div>
     

      <div className="infoList">
        <label className="infoItem"  style={{color: 'rgb(239 68 68)'}}
         
         >Vieti: {score}</label>
        <label className="infoItem" style={{color: 'rgb(234 179 8)'}}>Scor Actual: {correctCounter}</label>
        <label className="infoItem">Cel Mai Bun Scor: {bestScore}</label>
      </div>

      <span id="color-display">{pickedColor}</span>

      <div id="container">
        {colors.map((color, index) => (
          <div key={index} className="square" style={{ backgroundColor: color, transition:'all 0.4s ease' }} onClick={() => handleSquareClick(color,index)}></div>
        ))}
      </div>
    </>
  );
}

export default Joc;
