import React, { useEffect, useState } from 'react';
import './animatii.css';

function BubbleEffect() {
  const [bubble, setBubble] = useState(null);

  useEffect(() => {
    const createBubble = () => {
      const size = Math.random() * (80 - 20) + 20;
      const duration = Math.random() * (10 - 3) + 3;
      const left = Math.random() * 100;

      const newBubble = {
        id: new Date().getTime(),
        size,
        duration,
        left,
      };

      setBubble(newBubble);
      setTimeout(createBubble, duration * 1000);  // Reducem timpul de așteptare pentru o generare mai rapidă
    };

    createBubble();

    return () => clearTimeout(createBubble);
  }, []);

  return (
    <div className="relative w-full h-full overflow-hidden">
      {bubble && (
        <div
          key={bubble.id}
          className="absolute bg-blue-500 rounded-full opacity-60"
          style={{
            width: `${bubble.size}px`,
            height: `${bubble.size}px`,
            left: `${bubble.left}%`,
            bottom: `-150px`,
            animation: `rise ${bubble.duration}s linear infinite`
          }}
        />
      )}
    </div>
  );
}

export default BubbleEffect;
