import React, { useState } from 'react';

const ColorLab = () => {
    const [red, setRed] = useState(0);
    const [green, setGreen] = useState(0);
    const [blue, setBlue] = useState(0);

    const updateColor = (color, value) => {
        switch (color) {
            case 'red':
                setRed(value);
                break;
            case 'green':
                setGreen(value);
                break;
            case 'blue':
                setBlue(value);
                break;
            default:
                break;
        }
    };

    return (
        <div className="p-5">
            <div className="w-3/4 h-auto" style={{ backgroundColor: `rgb(${red}, ${green}, ${blue})` }}></div>
            <div className="mt-4">
                <label className="block">Red: {red}</label>
                <input type="range" min="0" max="255" value={red} onChange={(e) => updateColor('red', e.target.value)} className="w-full"/>
            </div>
            <div className="mt-4">
                <label className="block">Green: {green}</label>
                <input type="range" min="0" max="255" value={green} onChange={(e) => updateColor('green', e.target.value)} className="w-full"/>
            </div>
            <div className="mt-4">
                <label className="block">Blue: {blue}</label>
                <input type="range" min="0" max="255" value={blue} onChange={(e) => updateColor('blue', e.target.value)} className="w-full"/>
            </div>
        </div>
    );
};

export default ColorLab;
