import React, { useState } from 'react';

const ColorLab = () => {
    const [red, setRed] = useState(0);
    const [green, setGreen] = useState(0);
    const [blue, setBlue] = useState(0);

    const updateColor = (color, value) => {
        switch (color) {
            case 'red':
                setRed(value);
                break;
            case 'green':
                setGreen(value);
                break;
            case 'blue':
                setBlue(value);
                break;
            default:
                break;
        }
    };

    return (
        <div className="flex justify-center items-center p-5 min-h-screen w-full">
            <div className="flex flex-col items-center w-full">
                <div className="w-4/5  h-64 bg-red-500" style={{ backgroundColor: `rgb(${red}, ${green}, ${blue})` }}></div>
                <div className="mt-4 w-4/5">
                    <label className="block text-center">Red: {red}</label>
                    <input type="range" min="0" max="255" value={red} onChange={(e) => updateColor('red', e.target.value)} className="w-full"/>
                </div>
                <div className="mt-4 w-4/5">
                    <label className="block text-center">Green: {green}</label>
                    <input type="range" min="0" max="255" value={green} onChange={(e) => updateColor('green', e.target.value)} className="w-full"/>
                </div>
                <div className="mt-4 w-4/5 ">
                    <label className="block text-center">Blue: {blue}</label>
                    <input type="range" min="0" max="255" value={blue} onChange={(e) => updateColor('blue', e.target.value)} className="w-full"/>
                </div>
            </div>
        </div>
    );
};

export default ColorLab;
