import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import ButtonsComponent from './buttonComponent';

function Teorie() {
    const navigate = useNavigate();

    useEffect(() => {
        // Navighează către introducere la încărcarea inițială a paginii
        navigate('/teorie/introducere');
    }, [navigate]);

    return (
        <>
            <div className="flex h-screen">
                {/* Panoul cu butoane, ocupând o parte fixă a lățimii ecranului și rămânând în stânga */}
                <div className="w-1/6 bg-gray-800 p-4 overflow-auto">
                    <ButtonsComponent currentButton={1} />
                </div>

                {/* Conținutul principal al paginii de introducere, ocupând restul spațiului */}
                <div className="w-3/4 p-4 text-center flex flex-col justify-center items-center bg-gray-100">
                    <h1 className="text-2xl font-bold text-gray-800 mb-4">Bine ai venit la Introducere!</h1>
                    <p className="text-lg text-gray-700">Aceasta este o descriere generală a importanței și aplicării teoriei culorilor în design.</p>
                </div>
            </div>
        </>
    );
}

export default Teorie;
