import { useState } from "react"

export default function Setari(){
    const[theme, setTheme] = useState(true)

    function handleTheme(){
        setTheme(!theme)
    }
    return(
        <>
            <div className="flex w-full mx-5 my-10">
                <div className="block w-2/5">
                <h1>Selecteaza Tema</h1>
                </div>
                <div className="flex w-3/5 justify-center text-center items-center">
                <button className="w-1/5 py-3 bg-white rounded-lg mr-5 justify-center text-center flex"
                onClick={() => handleTheme(theme)}
                >Luminos </button>
                <button className="w-1/5 py-3 bg-slate-800 rounded-lg justify-center text-center flex"
                onClick={() => handleTheme(theme)}
                >Intunecat</button>
                </div>
            </div>
        </>
    )
}
