import React, { useState } from 'react';
import { SketchPicker } from 'react-color';

function PaletteCreator() {
    const [colors, setColors] = useState([]);
    const [selectedColor, setSelectedColor] = useState('#fff');

    const handleColorChange = (color) => {
        setSelectedColor(color.hex);
    };

    const addColor = () => {
        if (!colors.includes(selectedColor)) {
            setColors([...colors, selectedColor]);
        }
    };

    const removeColor = (colorToRemove) => {
        setColors(colors.filter(color => color !== colorToRemove));
    };

    return (
        <div className='w-full' style={{ padding: '20px', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <SketchPicker color={selectedColor} onChangeComplete={handleColorChange} />
            <button onClick={addColor} style={{ marginTop: '20px', fontSize: '16px', padding: '10px 20px' }}>
                Add Color
            </button>
            <div style={{ marginTop: '20px', display: 'flex', flexWrap: 'wrap' }}>
                {colors.map((color, index) => (
                    <div className='w-full h-64 ' key={index} style={{ backgroundColor: color }}>
                        <button onClick={() => removeColor(color)} style={{ color: 'white' }}>X</button>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default PaletteCreator;
