import React, { useState } from 'react';

const Img2 = () => {
  const [hoverColor, setHoverColor] = useState('');
  const colors = [
    "#FF0000", "#FF1A00", "#FF3500", "#FF4F00", "#FF6900", 
    "#FF8300", "#FF9D00", "#FFB800", "#FFD200", "#FFEC00",
    "#E6FF00", "#CCFF00", "#B2FF00", "#99FF00", "#7FFF00",
    "#66FF00", "#4CFF00", "#32FF00", "#19FF00", "#00FF00"
  ]; // Array of 20 RGB colors

  const radius = 100;
  const strokeWidth = 20;

  const calculatePath = (index, total) => {
    const angle = (index / total) * Math.PI * 2;
    const x = Math.cos(angle) * radius;
    const y = Math.sin(angle) * radius;
    return `M${radius},${radius} L${radius + x},${radius + y}`;
  };

  const handleMouseEnter = (color) => {
    setHoverColor(`RGB: ${color}, HEX: ${color}`);
  };

  return (
    <div className="relative flex flex-col items-center">
      <svg width={radius * 2 + strokeWidth} height={radius * 2 + strokeWidth} viewBox={`0 0 ${radius * 2 + strokeWidth} ${radius * 2 + strokeWidth}`} className="mx-auto">
        {colors.map((color, index) => (
          <path
            key={color}
            d={calculatePath(index, colors.length)}
            fill="none"
            stroke={color}
            strokeWidth={strokeWidth}
            onMouseEnter={() => handleMouseEnter(color)}
          />
        ))}
      </svg>
      {hoverColor && (
        <div className="absolute top-0 text-white">
          {hoverColor}
        </div>
      )}
    </div>
  );
};

export default Img2;
