// Teorie.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import ButtonsComponent from './buttonComponents';

function Teorie() {
    const navigate = useNavigate();

    const handleButtonClick = (buttonNumber) => {
        if (buttonNumber === 1) {
            // Deschide componenta Introducere direct în această pagină
            // Alternativ, poți folosi navigate('/introducere') dacă ai o rută definită
            navigate('/teorie/introducere');
        } 
    };

    return (
        <div className="flex flex-col items-center justify-center border text-center rounded mt-10" style={{width:'15%'}}>
           
            {Array.from({ length: 20 }, (_, index) => (
                <button 
                    key={index + 1}
                    onClick={() => handleButtonClick(index + 1)}
                    className={`w-full my-1 py-2 text-sm font-medium text-white rounded transition-colors duration-200
                                
                                focus:outline-none focus:ring-2 focus:ring-blue-700 focus:ring-opacity-50`}
                >
                    {index + 1 === 1 ? 'Introducere' : `Buton ${index + 1}`}
                </button>
            ))}
        </div>
    );
}

export default Teorie;
