import ButtonsComponent from "./buttonComponents"

export default function Sistemele(){
    const [color, setColor] = useState({r: 255, g: 255, b: 0, a: 1});

    // Functie pentru actualizarea culorii și sistemul de culoare
    const handleChange = (e) => {
        const { name, value } = e.target;
        setColor(prev => ({ ...prev, [name]: value }));
    };
    return(
        <>
       <div class="flex h-auto">
         <div className="w-1/6 bg-gray-600 p-4 overflow-auto">
                <ButtonsComponent currentButton={1} />
            </div>
            <div className="w-5/6 p-4 flex flex-col bg-gray-100">\
            <h2>Color Combiner Tool</h2>
            <input type="number" name="r" value={color.r} onChange={handleChange} />
            <input type="number" name="g" value={color.g} onChange={handleChange} />
            <input type="number" name="b" value={color.b} onChange={handleChange} />
            <input type="number" name="a" value={color.a} onChange={handleChange} step="0.1" min="0" max="1" />
            <div style={{ height: '50px', width: '100px', backgroundColor: `rgba(${color.r}, ${color.g}, ${color.b}, ${color.a})` }}>
                Preview Color
            </div>
            
            </div>
           
            </div>

        </>
    )
}
