import React, { useState } from 'react';

function RoataCulorilorComplementareImpartite() {
    const [segmentSelectat, setSegmentSelectat] = useState(null);

    // Configurarea culorilor într-o manieră care nu le plasează una lângă alta
    const culori = [
        'hsl(0, 80%, 50%)', // Roșu
        'hsl(120, 80%, 50%)', // Verde (complementara roșului)
        'hsl(240, 80%, 50%)', // Albastru
        'hsl(60, 80%, 50%)', // Galben (complementara albastrului)
        'hsl(180, 80%, 50%)', // Turcoaz
        'hsl(300, 80%, 50%)', // Violet (complementara turcoazului)
        'hsl(30, 80%, 50%)', // Portocaliu
        'hsl(270, 80%, 50%)'  // Indigo (complementara portocaliului)
    ];

    const getCoordinatesForPercent = (percent) => {
        const x = Math.cos(2 * Math.PI * percent);
        const y = Math.sin(2 * Math.PI * percent);
        return [x, y];
    };

    const getPathData = (index, total) => {
        const start = getCoordinatesForPercent(index / total);
        const end = getCoordinatesForPercent((index + 1) / total);

        return `M0,0 L${start[0] * 16},${start[1] * 16} A16,16 0 0,1 ${end[0] * 16},${end[1] * 16}Z`;
    };

    const ajusteazaCuloare = (index) => {
        const complementIndex = (index + 4) % 8; // Calculul indexului complementar
        if (segmentSelectat === null || index === segmentSelectat || index === complementIndex) {
            return culori[index];
        }
        return 'grey';
    };

    return (
        <div className="text-center">
            <h1 className="text-2xl font-bold my-4">Roata Culorilor Complementare Împărțite</h1>
            <svg viewBox="-18 -18 36 36" width="360px" height="360px">
                {culori.map((_, index) => (
                    <path
                        key={index}
                        d={getPathData(index, culori.length)}
                        fill={ajusteazaCuloare(index)}
                        onClick={() => setSegmentSelectat(index)}
                        style={{ cursor: 'pointer', stroke: 'black', strokeWidth: 0.1 }}
                    />
                ))}
            </svg>
            <p className="mt-4">Selectați o culoare pentru a evidenția aceasta și culoarea sa complementară. Restul vor deveni gri.</p>
        </div>
    );
}

export default RoataCulorilorComplementareImpartite;
