import React, { useState } from 'react';

function RoataMonocromatica() {
    const [selectedSegment, setSelectedSegment] = useState(null);

    const colors = [
        '#FF0000', '#FFA500', '#FFFF00', '#808000',
        '#008000', '#008080', '#00FFFF', '#0000FF',
        '#800080', '#FF00FF', '#800000', '#000080'
    ];

    const handleSegmentClick = (index) => {
        setSelectedSegment(index);
    };

    return (
        <div className="container mx-auto px-4 py-6 text-center">
            <h1 className="text-2xl font-bold mb-4">Roata Monocromatică</h1>
            <svg viewBox="0 0 36 36" className="w-64 h-64 mx-auto">
                {colors.map((color, index) => (
                    <path
                        key={index}
                        d={`M18 18 L18 2 A 16 16 0 0 1 ${18 + 16 * Math.sin(Math.PI * 2 * (index + 1) / 12)} ${18 - 16 * Math.cos(Math.PI * 2 * (index + 1) / 12)} Z`}
                        fill={selectedSegment === index ? color : '#D3D3D3'}
                        stroke="white"
                        strokeWidth="0.5"
                        onClick={() => handleSegmentClick(index)}
                    />
                ))}
            </svg>
            <p className="mt-4">Selectați un segment pentru a afișa culoarea sa monocromatică, restul vor deveni gri.</p>
        </div>
    );
}

export default RoataMonocromatica;
