import React, { useState } from 'react';

const RoataDublaComplementara = () => {
  const culori = [
    "rgb(255, 69, 0)",    // redOrange
    "rgb(255, 0, 0)",     // red
    "rgb(199, 21, 133)",  // violet-red
    "rgb(238, 130, 238)", // violet
    "rgb(138, 43, 226)",  // blue-violet
    "rgb(0, 0, 255)",     // blue
    "rgb(0, 139, 139)",   // blue-green
    "rgb(0, 128, 0)",     // green
    "rgb(173, 255, 47)",  // yellowGreen
    "rgb(255, 255, 0)",   // yellow
    "rgb(255, 179, 0)",   // orangeyellow
    "rgb(255, 165, 0)",   // orange
  ];

  const [selectedColorIndex, setSelectedColorIndex] = useState(null);

  const radius = 150;
  const cx = 200;
  const cy = 200;

  const getCoordinatesForAngle = (angle, radius) => {
    let rad = (angle * Math.PI) / 180;
    return {
      x: cx + radius * Math.cos(rad),
      y: cy + radius * Math.sin(rad)
    };
  };

  const getPathData = (index, total, radius) => {
    const angle = 360 / total;
    const startAngle = angle * index;
    const endAngle = startAngle + angle;
    const largeArc = angle > 180 ? 1 : 0;

    const start = getCoordinatesForAngle(startAngle, radius);
    const end = getCoordinatesForAngle(endAngle, radius);

    return `M${cx},${cy} L${start.x},${start.y} A${radius},${radius} 0 ${largeArc},1 ${end.x},${end.y} Z`;
  };

  const handleClick = index => {
    setSelectedColorIndex(index);
  };

  return (
    <svg width="400" height="400" viewBox="0 0 400 400">
      {culori.map((culoare, index) => (
        <path
          key={index}
          d={getPathData(index, culori.length, radius)}
          fill={culoare}
          stroke={selectedColorIndex === index || selectedColorIndex !== null && (index === (selectedColorIndex + culori.length / 2) % culori.length) ? 'black' : 'none'}
          strokeWidth="2"
          onClick={() => handleClick(index)}
        />
      ))}
      <text x="50%" y="50%" dominantBaseline="middle" textAnchor="middle" fill="black" fontSize="16">
        {selectedColorIndex !== null ? `Culoare selectată: ${culori[selectedColorIndex]}` : 'Selectează o culoare'}
      </text>
    </svg>
  );
};

export default RoataDublaComplementara;
