import React, { useEffect, useState } from 'react';
import './animatii'
function BubbleEffect() {
  const [bubbles, setBubbles] = useState([]);

  useEffect(() => {
    const interval = setInterval(() => {
      const size = Math.random() * (80 - 20) + 20; 
      const duration = Math.random() * (15 - 5) + 5;
      const left = 1; 

      const bubble = {
        id: bubbles.length,
        size,
        duration,
        left,
      };

      setBubbles(currentBubbles => [...currentBubbles, bubble]);

      if (bubbles.length > 10) {
        setBubbles(currentBubbles => currentBubbles.slice(1));
      }
    }, 500);

    return () => clearInterval(interval);
  }, [bubbles]);

  return (
    <div className="relative w-full h-full overflow-hidden">
     {
  bubbles.map(bubble => (
    <div
      key={bubble.id}
      className="absolute bg-blue-500 rounded-full opacity-60"
      style={{
        width: `${bubble.size}px`,
        height: `${bubble.size}px`,
        left: `${bubble.left}%`,
        bottom: `-150px`, // Ajustează acesta dacă este necesar
        animation: `rise ${bubble.duration}s linear infinite`
      }}
    />
  ))
}

    </div>
  );
}

export default BubbleEffect;
