import React, { useState } from 'react';
import './laborator.css';

const ColorLab = () => {
    const [red, setRed] = useState(0);
    const [green, setGreen] = useState(0);
    const [blue, setBlue] = useState(0);
    const [alpha, setAlpha] = useState(1);
    const [colorMode, setColorMode] = useState('rgb'); // 'rgb', 'rgba', 'hsl', 'cmyk', 'xyz', 'cielab'

    const updateColor = (color, value) => {
        value = Number(value);
        if ((color === 'alpha' ? (value >= 0 && value <= 1) : (value >= 0 && value <= 255))) {
            switch (color) {
                case 'red':
                    setRed(value);
                    break;
                case 'green':
                    setGreen(value);
                    break;
                case 'blue':
                    setBlue(value);
                    break;
                case 'alpha':
                    setAlpha(value);
                    break;
                default:
                    break;
            }
        }
    };

    const rgbToHSL = (r, g, b) => {
       
        r /= 255;
        g /= 255;
        b /= 255;
    
        const max = Math.max(r, g, b);
        const min = Math.min(r, g, b);
        let h, s, l = (max + min) / 2;
    
        if (max === min) {
            h = s = 0; 
        } else {
            const d = max - min;
            s = l > 0.5 ? d / (2 - max - min) : d / (max + min);
            switch (max) {
                case r:
                    h = (g - b) / d + (g < b ? 6 : 0);
                    break;
                case g:
                    h = (b - r) / d + 2;
                    break;
                case b:
                    h = (r - g) / d + 4;
                    break;
                default:
                    break;
            }
            h /= 6;
        }
    
        h = Math.round(h * 360); 
        s = Math.round(s * 100); 
        l = Math.round(l * 100); 
        return `HSL(${h}, ${s}%, ${l}%)`;
    };
    
    const rgbToCMYK = (r, g, b) => {
        r /= 255;
        g /= 255;
        b /= 255;
    
        const k = 1 - Math.max(r, g, b);
        let c = (1 - r - k) / (1 - k) || 0;
        let m = (1 - g - k) / (1 - k) || 0;
        let y = (1 - b - k) / (1 - k) || 0;
    
        c = Math.round(c * 100);
        m = Math.round(m * 100);
        y = Math.round(y * 100);
        k = Math.round(k * 100);
    
        return `CMYK(${c}%, ${m}%, ${y}%, ${k}%)`;
    };
    

    const rgbToXYZ = (r, g, b) => `XYZ(${r}, ${g}, ${b})`;
    const rgbToCIELAB = (r, g, b) => `CIE LAB(${r % 100}, ${g % 100}, ${b % 100})`;

    const getColorString = () => {
        switch (colorMode) {
            case 'rgba':
                return `rgba(${red}, ${green}, ${blue}, ${alpha})`;
            case 'hsl':
                return rgbToHSL(red, green, blue);
            case 'cmyk':
                return rgbToCMYK(red, green, blue);
            case 'xyz':
                return rgbToXYZ(red, green, blue);
            case 'cielab':
                return rgbToCIELAB(red, green, blue);
            default:
                return `rgb(${red}, ${green}, ${blue})`;
        }
    };

    return (
        <div className="flex justify-center p-5 min-h-screen w-full">
            <div className="flex flex-col items-center w-4/5">
                <div className="h-64 w-full" style={{ backgroundColor: getColorString() }}></div>

                <select className="w-1/5 mt-5" value={colorMode} onChange={(e) => setColorMode(e.target.value)}>
                    <option value="rgb">RGB</option>
                    <option value="rgba">RGBA</option>
                    <option value="hsl">HSL</option>
                    <option value="cmyk">CMYK</option>
                    <option value="xyz">XYZ</option>
                    <option value="cielab">CIE LAB</option>
                </select>

                <h1 className="text-end mt-5">Color Settings</h1>

                <div className="flex mt-4 w-full justify-start">
                    <label className="mr-2 w-1/5 text-3xl">Red:</label>
                    <input className="w-3/5 h-8" type="range" min="0" max="255" value={red} onChange={(e) => updateColor('red', e.target.value)} />
                    <input type="number" value={red} onChange={(e) => updateColor('red', e.target.value)} className="w-1/5 text-center ml-10 h-8"/>
                </div>
                <div className="flex mt-4 w-full justify-start">
                    <label className="mr-2  w-1/5 text-3xl">Green:</label>
                    <input type="range" min="0" max="255" value={green} onChange={(e) => updateColor('green', e.target.value)} className="w-3/5 h-8"/>
                    <input type="number" value={green} onChange={(e) => updateColor('green', e.target.value)} className="w-1/5 text-center ml-10 h-8"/>
                </div>
                <div className="flex mt-4 w-full justify-start">
                    <label className="mr-2  w-1/5 text-3xl">Blue:</label>
                    <input type="range" min="0" max="255" value={blue} onChange={(e) => updateColor('blue', e.target.value)} className="w-3/5 h-8"/>
                    <input type="number" value={blue} onChange={(e) => updateColor('blue', e.target.value)} className="w-1/5 text-center ml-10 h-8"/>
                </div>
                {colorMode === 'rgba' && (
                    <div className="flex mt-4 w-full justify-start">
                        <label className="mr-2  w-1/5 text-3xl">Alpha:</label>
                        <input type="range" min="0" max="1" step="0.01" value={alpha} onChange={(e) => updateColor('alpha', e.target.value)} className="w-3/5 h-8"/>
                        <input type="number" value={alpha} onChange={(e) => updateColor('alpha', e.target.value)} className="w-1/5 text-center ml-10 h-8"/>
                    </div>
                )}
            </div>
        </div>
    );
};

export default ColorLab;
