import React, { useState } from 'react';

function RoataCulorilorComplementare() {
    const [selectedSegment, setSelectedSegment] = useState(null);

    const colors = [
        'hsl(0, 80%, 50%)',    // Roșu
      
        'hsl(240, 80%, 50%)',  // Albastru

        'hsl(60, 80%, 50%)',   // Galben
        'hsl(120, 80%, 50%)',  // Verde (complementara roșului)
        'hsl(30, 80%, 50%)',   // Portocaliu (complementara albastrului)
        'hsl(270, 80%, 50%)',  // Violet (complementara galbenului)
        'hsl(180, 80%, 50%)',  // Turcoaz
        'hsl(330, 80%, 50%)'   // Roșu-violet (complementara turcoazului)
    ];

    const getCoordinatesForPercent = (percent) => {
        const x = Math.cos(2 * Math.PI * percent);
        const y = Math.sin(2 * Math.PI * percent);
        return [x, y];
    };

    const getPathData = (index, segments) => {
        const startPercent = index / segments;
        const endPercent = (index + 1) / segments;
        const [startX, startY] = getCoordinatesForPercent(startPercent);
        const [endX, endY] = getCoordinatesForPercent(endPercent);

        return `M 0 0 L ${startX * 16} ${startY * 16} A 16 16 0 0 1 ${endX * 16} ${endY * 16} z`;
    };

    const adjustColor = (index) => {
        if (selectedSegment !== null) {
            // Verifică dacă indexul este egal cu segmentul selectat sau cu complementara acestuia
            if (index === selectedSegment || index === (selectedSegment + 1) % 8) {
                return colors[index];
            }
            return 'grey';
        }
        return colors[index]; // Dacă nu este selectat niciun segment, afișează toate culorile
    };

    return (
        <div className="text-center">
            <h1 className="text-2xl font-bold my-4">Roata Culorilor Complementare</h1>
            <svg viewBox="-18 -18 36 36" width="300px" height="300px">
                {colors.map((_, index) => (
                    <path
                        key={index}
                        d={getPathData(index, 8)}
                        fill={adjustColor(index)}
                        onClick={() => setSelectedSegment(index % 2 === 0 ? index : index - 1)}
                        style={{ cursor: 'pointer' }}
                    />
                ))}
            </svg>
            <p className="mt-4">Selectați o culoare pentru a evidenția aceasta și culoarea sa complementară. Restul vor deveni gri.</p>
        </div>
    );
}

export default RoataCulorilorComplementare;
