import React from 'react';
import './animatieTitlu.css'; // Asigură-te că acest fișier CSS este în același folder cu componenta

const WelcomeAnimation = () => {
  const text = "Bine ați venit";
  const characters = text.split('');

  return (
    <div className="welcome-container">
      <ul className="fly-in-text">
        {characters.map((char, index) => (
          <li key={index} style={{ animationDelay: `${index * 0.1}s` }}>
            {char !== ' ' ? char : '\u00A0'}  {/* Utilizează caracterul de spațiu non-breaking pentru spații */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default WelcomeAnimation;
