import React, { useState, useEffect } from 'react';
import { SketchPicker } from 'react-color';

function CreatorPaleta() {
    const [palete, setPalete] = useState([]);
    const [culori, setCulori] = useState([]);
    const [culoareSelectata, setCuloareSelectata] = useState('#ffffff');
    const [numePaleta, setNumePaleta] = useState('');

    // Încărcarea tuturor paletelor salvate din localStorage când componenta este montată
    useEffect(() => {
        const paleteSalvate = JSON.parse(localStorage.getItem('paleteCulori'));
        if (paleteSalvate) {
            setPalete(paleteSalvate);
        }
    }, []);

    const schimbaCuloare = (color) => {
        setCuloareSelectata(color.hex);
    };

    const adaugaCuloare = () => {
        if (!culori.includes(culoareSelectata)) {
            setCulori([...culori, culoareSelectata]);
        }
    };

    const eliminaCuloare = (culoareDeEliminat) => {
        setCulori(culori.filter(culoare => culoare !== culoareDeEliminat));
    };

    const salveazaPaleta = () => {
        const nouaPaleta = { nume: numePaleta, culori };
        const paleteActualizate = [...palete, nouaPaleta];
        setPalete(paleteActualizate);
        localStorage.setItem('paleteCulori', JSON.stringify(paleteActualizate));
        setCulori([]);
        setNumePaleta('');
    };

    const incarcaPaleta = (paleta) => {
        setCulori(paleta.culori);
        setNumePaleta(paleta.nume);
    };

    return (
        <div className="flex h-screen bg-gray-100 p-4">
            <div className="w-1/2 flex flex-col items-center justify-center">
                <input
                    className="mb-2 p-1 border rounded"
                    placeholder="Nume Paleta"
                    value={numePaleta}
                    onChange={e => setNumePaleta(e.target.value)}
                />
                <SketchPicker color={culoareSelectata} onChangeComplete={schimbaCuloare} />
                <button className="mt-4 bg-blue-500 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" onClick={adaugaCuloare}>
                    Adaugă Culoare
                </button>
                <button className="mt-2 bg-green-500 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" onClick={salveazaPaleta}>
                    Salvează Paleta
                </button>
            </div>
            <div className="w-1/2 flex flex-col items-center justify-start overflow-auto">
                <h2 className="text-lg font-bold py-2">Palete Salvate</h2>
                {palete.map((paleta, index) => (
                    <button key={index} className="m-1 bg-purple-300 text-white py-1 px-3 rounded" onClick={() => incarcaPaleta(paleta)}>
                        {paleta.nume}
                    </button>
                ))}
            </div>
        </div>
    );
}

export default CreatorPaleta;
