import React, { useState } from 'react';
import './quiz.css';
import Introducere from './Introducere.json';
import Istoria from './Istoria.json';
import Perceptia from './Perceptia.json';
import Contrastul_in_Web_Design_si_Tehnologie from './Contrastul_in_Web_Design_si_Tehnologie.json';
import Contrastul_in_arta from './Contrastul_in_arta.json';
import Durabilitatea from './Durabilitatea.json';
import Lectie_1 from './Lectie_1.json';
import Lectie_2 from './Lectie_2.json';
import Lectie_3 from './Lectie_3.json';
import Lectie_4 from './Lectie_4.json';

export default function Quiz() {
    const quizNames = [
        'Introducere', 'Istoria', 'Perceptia', 'Contrastul in Web Design si Tehnologie', 
        'Contrastul in arta', 'Durabilitatea', 'Lectie 1', 'Lectie 2', 'Lectie 3', 'Lectie 4'
    ];

    const quizData = {
        Introducere,
        Istoria,
        Perceptia,
        Contrastul_in_Web_Design_si_Tehnologie,
        Contrastul_in_arta,
        Durabilitatea,
        Lectie_1,
        Lectie_2,
        Lectie_3,
        Lectie_4
    };

    const [currentQuiz, setCurrentQuiz] = useState('');

    const handleQuizSelection = (quizName) => {
        setCurrentQuiz(quizData[quizName.replace(/\s/g, '_')]);
    };

    return (
        <div className="flex h-auto bg-gray-400 p-4 pb-10">
            <div className="w-full flex xl:columns-3 flex-wrap gap-8 justify-center items-center lg:columns-2 sm:columns-1">
                {quizNames.map((name, index) => (
                    <button
                        key={index}
                        className="btn1"
                        onClick={() => handleQuizSelection(name)}
                    >
                        {name}
                    </button>
                ))}
            </div>
            {currentQuiz && (
                <div>
                    <h1>{currentQuiz.title}</h1>
                    {currentQuiz.questions.map((question, idx) => (
                        <div key={idx}>
                            <p>{question.question}</p>
                            {question.options.map((option, i) => (
                                <button key={i}>{option}</button>
                            ))}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}
