import React from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import Nuj from './Teorie/teorie';
import Joc from './Joc/joc';
import Settings from './Setari/setari';
import Acasa from './Acasa/acasa';
import Laborator from './Laborator/laborator';
import Quiz from './Quizz-uri/quiz';
import Nav from './Navigatie/navbar';

import './index.css';

function App() {
  const location = useLocation();  // Accesează locația curentă

  return (
    <>
      {location.pathname === '/' && <Nav />}  // Afisează Nav doar pe pagina de start
      <Router>
        <div className="App">
          <Routes>
            <Route path="/" element={<Acasa />} />
            <Route path="/joc" element={<Joc />} />
            <Route path="/teorie" element={<Nuj />} />
            <Route path="/laborator" element={<Laborator />} />
            <Route path="/setari" element={<Settings />} />
            <Route path="/quiz/*" element={<Quiz />} />
          </Routes>
        </div>
      </Router>
    </>
  );
}

export default App;
