import React, { useState } from 'react';

const intrebari = [
    {
        intrebare: "Ce mineral are o culoare rosiatica ?",
        optiuni: ["carbune",  "ocru" , "hematit", "quartz"],
        raspunsCorect: "ocru"
    },
    {
        intrebare: "Pentru decorarea templelor si a mormintelor egiptenii au iventat ..... egiptean",
        optiuni: ["albastru", "rosul", "verdele", "galbenul"],
        raspunsCorect: "albastru"
    },
    {
        intrebare: "In Roma si Grecia Antica culoarea regalitatii este ?",
        optiuni: ["galben", "albastru", "rosu", "mov"],
        raspunsCorect: "mov"
    },
    {
        intrebare: "In ce epoca utilizarea pigmentilor a fost in concordanta cu religia",
        optiuni: ["Evul Mediu", "Antichitate", "Epoca Moderna", "Preistoria"],
        raspunsCorect: "Evul Mediu"
    },
    {
        intrebare: "Care dintre urmatorii artisti NU este renancestist ?",
        optiuni: ["Vincent van Gogh", "Rafael", "Michelangelo", "Tiziano"],
        raspunsCorect: "Vincent van Gogh"
    },
    {
        intrebare: "Ce a imbunatatit calitatea di durabilitatea vopselelor?",
        optiuni: ["inovatiile tehnologice", "metode avansate"],
        raspunsCorect: "inovatiile tehnologice"
    },
    {
        intrebare: "Cubismul și Artă Modernă au folosit culoarea pentru a transmite nu doar imagini, ci si ..... ?",
        optiuni: ["emoții", "critici sociale"],
        raspunsCorect:[ "emotii", "critici sociale" ]
    },
   
   
   
   
   
];

function QuizPerceptiaCulorilor() {
    const [intrebareCurenta, setIntrebareCurenta] = useState(0);
    const [optiuniSelectate, setOptiuniSelectate] = useState([]);
    const [arataRezultate, setArataRezultate] = useState(false);

    const selecteazaOptiune = (optiune) => {
        const newOptiuniSelectate = [...optiuniSelectate];
        newOptiuniSelectate[intrebareCurenta] = optiune;
        setOptiuniSelectate(newOptiuniSelectate);

        const esteUltimaIntrebare = intrebareCurenta === intrebari.length - 1;
        if (esteUltimaIntrebare) {
            setArataRezultate(true);
        } else {
            setIntrebareCurenta(intrebareCurenta + 1);
        }
    };

    const restartQuiz = () => {
        setIntrebareCurenta(0);
        setOptiuniSelectate([]);
        setArataRezultate(false);
    };

    return (
        <div className="container mx-auto p-4">
            {arataRezultate ? (
                <div>
                    <h1>Rezultatele Quizului</h1>
                    <p>Ai obținut {optiuniSelectate.filter((opt, index) => intrebari[index].raspunsCorect === opt).length} din {intrebari.length}</p>
                    <div>
                        {intrebari.map((intrebare, index) => {
                            const raspunsCorect = intrebare.raspunsCorect;
                            const raspunsUtilizator = optiuniSelectate[index];
                            return raspunsUtilizator !== raspunsCorect ? (
                                <div key={index}>
                                    <p>{intrebare.intrebare}</p>
                                    <p>Răspunsul tău: {raspunsUtilizator}</p>
                                    <p>Răspuns corect: {raspunsCorect}</p>
                                </div>
                            ) : null;
                        })}
                    </div>
                    <button onClick={restartQuiz} className="ml-10 mt-5 p-4 bg-blue-500 text-white rounded hover:bg-blue-700 flex justify-center items-center">
                        Restart Quiz
                    </button>
                </div>
            ) : (
                <div>
                    <h2 className="text-center">{intrebari[intrebareCurenta].intrebare}</h2>
                    <div>
                        {intrebari[intrebareCurenta].optiuni.map((optiune, index) => (
                            <button key={index} onClick={() => selecteazaOptiune(optiune)} className="w-full my-2 py-3 rounded-lg text-blue-900 hover:bg-gray-600 hover:text-white duration-500">
                                {optiune}
                            </button>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
}

export default QuizPerceptiaCulorilor;
