// Teorie.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';
import ButtonsComponent from './buttonComponents';

function Teorie() {
    const navigate = useNavigate();

    const handleButtonClick = (buttonNumber) => {
        if (buttonNumber === 1) {
            // Deschide componenta Introducere direct în această pagină
            // Alternativ, poți folosi navigate('/introducere') dacă ai o rută definită
            navigate('/teorie/introducere');
        } else {
            navigate(`/teorie/${buttonNumber}`);
        }
    };

    return (
        <>
        <div className="flex h-screen">
            {/* Panoul cu butoane, ocupând o parte fixă a lățimii ecranului și rămânând în stânga */}
            <div className="w-1/6 bg-gray-800 p-4 overflow-auto">
                <ButtonsComponent currentButton={1} />
            </div>

            {/* Conținutul principal al paginii de introducere, ocupând restul spațiului */}
            <div className="w-3/4 p-4 text-center flex flex-col justify-center items-center bg-gray-100">
                <h1 className="text-2xl font-bold text-gray-800 mb-4">Bine ai venit la Introducere!</h1>
                <p className="text-lg text-gray-700">Aceasta este o descriere generală a importanței și aplicării teoriei culorilor în design.</p>
            </div>
        </div>
        <div className="flex flex-col items-center justify-center border text-center rounded mt-10" style={{width:'15%'}}>
           
            {Array.from({ length: 20 }, (_, index) => (
                <button 
                    key={index + 1}
                    onClick={() => handleButtonClick(index + 1)}
                    className="w-full my-2 px-4 py-1 rounded bg-blue-500 text-white hover:bg-blue-700 focus:outline-none"
                >
                    {index + 1 === 1 ? 'Introducere' : `Buton ${index + 1}`}
                </button>
            ))}
        </div>
        </>
    );
}

export default Teorie;
