import React, { useState } from 'react';

function QuizDetails({ quizData }) {
    // Starea pentru a ține evidența opțiunilor selectate de utilizator
    const [selectedAnswers, setSelectedAnswers] = useState({});

    // Functia care se apelează la click pe un răspuns
    const handleOptionClick = (questionIndex, option) => {
        // Setăm răspunsul selectat pentru întrebarea curentă
        setSelectedAnswers({
            ...selectedAnswers,
            [questionIndex]: option
        });
    };

    // Verificăm dacă răspunsul selectat este corect
    const isAnswerCorrect = (questionIndex, option) => {
        return quizData.questions[questionIndex].raspunsCorect === option;
    };

    return (
        <div className="quiz-details">
            <h1>{quizData.title || "Detalii Quiz"}</h1>
            {quizData.questions.map((question, idx) => (
                <div key={idx} className="question-block">
                    <p>{question.intrebare}</p>
                    <div className="options-container">
                        {question.optiuni.map((option, index) => (
                            <button 
                                key={index} 
                                className={`option-button ${selectedAnswers[idx] === option ? 'selected' : ''}`}
                                onClick={() => handleOptionClick(idx, option)}
                            >
                                {option}
                            </button>
                        ))}
                    </div>
                    {selectedAnswers[idx] && (
                        <div className={`answer-feedback ${isAnswerCorrect(idx, selectedAnswers[idx]) ? 'correct' : 'incorrect'}`}>
                            {isAnswerCorrect(idx, selectedAnswers[idx]) ? 'Răspuns corect!' : 'Răspuns greșit!'}
                        </div>
                    )}
                </div>
            ))}
        </div>
    );
}

export default QuizDetails;
