import React, { useState } from 'react';

const intrebari = [
    {
            question: "Ce culoare este asociată cu profesionalismul și încrederea?",
            options: ["Albastru", "Portocaliu", "Negru", "Alb"],
            answer: "Albastru"
    },
    {
            question: "Ce culoare simbolizează luxul și calitatea?",
            options: ["Gri", "Mov", "Verde", "Galben"],
            answer: "Mov"
    },   
      {
        intrebare: "Ce culoare evocă pasiune și energie?",
        optiuni: ["Verde", "Roșu", "Albastru", "Gri"],
        raspunsCorect: "Roșu"
      },
      {
        intrebare: "Care culoare este asociată cu relaxarea și este odihnitoare pentru ochi?",
        optiuni: ["Galben", "Mov", "Verde", "Portocaliu"],
        raspunsCorect: "Verde"
      },
      {
        intrebare: "Care culoare stimulează apetitul în restaurante?",
        optiuni: ["Portocaliu", "Albastru", "Negru", "Alb"],
        raspunsCorect: "Portocaliu"
      },
      {
        intrebare: "Care culoare stimulează apetitul ?",
        optiuni: ["Portocaliu", "Albastru", "Negru", "Alb"],
        raspunsCorect: "Portocaliu"
      },
      {
        question: "Care culoare este percepută ca puritate?",
        options: ["Alb", "Negru", "Gri", "Roșu"],
        answer: "Alb"
      },
      {
        question: "Ce culoare este adesea asociată cu doliul în culturile estice?",
        options: ["Alb", "Negru", "Mov", "Roșu"],
        answer: "Alb"
      },
      {
        question: "Ce culoare sugerează sofisticare și stabilitate?",
        options: ["Gri", "Albastru", "Verde", "Galben"],
        answer: "Gri"
      }
      
      
    ];
    
    function QuizPerceptiaCulorilor() {
        const [intrebareCurenta, setIntrebareCurenta] = useState(0);
        const [optiuniSelectate, setOptiuniSelectate] = useState([]);
        const [arataRezultate, setArataRezultate] = useState(false);
      
        const selecteazaOptiune = (optiune) => {
          const noiOptiuniSelectate = [...optiuniSelectate];
          noiOptiuniSelectate[intrebareCurenta] = optiune;
          setOptiuniSelectate(noiOptiuniSelectate);
      
          if (intrebareCurenta === intrebari.length - 1) {
            setArataRezultate(true);
          } else {
            setIntrebareCurenta(intrebareCurenta + 1);
          }
        };
      
        const restartQuiz = () => {
          setIntrebareCurenta(0);
          setOptiuniSelectate([]);
          setArataRezultate(false);
        };
      
        return (
          <div className="container mx-auto p-4">
            {arataRezultate ? (
              <div>
                <h1>Rezultatele Quizului</h1>
                <p>Ai obținut {optiuniSelectate.filter((opt, index) => intrebari[index].raspunsCorect === opt).length} din {intrebari.length}</p>
                <p>Întrebări greșite:</p>
                {intrebari.map((intrebare, index) => {
                  const raspunsCorect = intrebare.raspunsCorect;
                  const raspunsUtilizator = optiuniSelectate[index];
                  if (raspunsUtilizator !== raspunsCorect) {
                    return (
                      <div key={index}>
                        <p>{intrebare.intrebare}</p>
                        <p>Răspunsul tău: {raspunsUtilizator}</p>
                        <p>Răspuns corect: {raspunsCorect}</p>
                      </div>
                    );
                  }
                  return null;
                })}
                <button onClick={restartQuiz} className="ml-10 mt-5 p-4 bg-blue-500 text-white rounded hover:bg-blue-700 flex justify-center items-center">
                  Restart Quiz
                </button>
              </div>
            ) : (
              <div>
                <h2>{intrebari[intrebareCurenta].intrebare}</h2>
                {intrebari[intrebareCurenta].optiuni.map((optiune, index) => (
                  <button key={index} onClick={() => selecteazaOptiune(optiune)} className="block w-full text-center my-2 p-2 bg-blue-500 text-white rounded hover:bg-blue-700">
                    {optiune}
                  </button>
                ))}
              </div>
            )}
          </div>
        );
      }
      
      export default QuizPerceptiaCulorilor;
