import React, { useState } from 'react';

function QuizDetails({ quizData }) {
    const [selectedAnswers, setSelectedAnswers] = useState({});

    // Functia pentru a actualiza răspunsurile alese
    const handleOptionClick = (questionIndex, option) => {
        setSelectedAnswers({
            ...selectedAnswers,
            [questionIndex]: option
        });
    };

    // Functia pentru a verifica dacă răspunsul selectat este corect
    const isAnswerCorrect = (questionIndex) => {
        return quizData.questions[questionIndex].raspunsCorect === selectedAnswers[questionIndex];
    };

    return (
        <div>
            <h1>{quizData.title || "Detalii Quiz"}</h1>
            
        </div>
    );
}

export default QuizDetails;
