import React, { useState } from 'react';

function RoataCulorilorSplitComplementary() {
    const [segmentSelectat, setSegmentSelectat] = useState(null);

    // Lista de culori cu perechile lor complementare împărțite
    const culori = [
        { principal: 'hsl(0, 80%, 50%)', split: ['hsl(150, 80%, 50%)', 'hsl(210, 80%, 50%)'] }, // Roșu
        { principal: 'hsl(120, 80%, 50%)', split: ['hsl(270, 80%, 50%)', 'hsl(330, 80%, 50%)'] }, // Verde
        { principal: 'hsl(240, 80%, 50%)', split: ['hsl(30, 80%, 50%)', 'hsl(90, 80%, 50%)'] }, // Albastru
        { principal: 'hsl(60, 80%, 50%)', split: ['hsl(150, 80%, 50%)', 'hsl(210, 80%, 50%)'] }  // Galben
    ];

    const getPathData = (index, total) => {
        const angle = (index / total) * Math.PI * 2 - Math.PI / 2;
        const x = Math.cos(angle);
        const y = Math.sin(angle);
        return [x, y];
    };

    const ajusteazaCuloare = (index) => {
        if (segmentSelectat === null || index === segmentSelectat || culori[segmentSelectat].split.includes(culori[index].principal)) {
            return culori[index].principal;
        }
        return 'grey';
    };

    return (
        <div className="text-center">
            <h1 className="text-2xl font-bold my-4">Roata Culorilor Complementare Împărțite</h1>
            <svg viewBox="-18 -18 36 36" width="360px" height="360px">
                {culori.map((c, index) => (
                    <path
                        key={index}
                        d={`M0,0 L${getPathData(index, culori.length)[0] * 16},${getPathData(index, culori.length)[1] * 16} A16,16 0 0,1 ${getPathData(index + 1, culori.length)[0] * 16},${getPathData(index + 1, culori.length)[1] * 16}Z`}
                        fill={ajusteazaCuloare(index)}
                        onClick={() => setSegmentSelectat(index)}
                        style={{ cursor: 'pointer', stroke: 'black', strokeWidth: 0.1 }}
                    />
                ))}
            </svg>
            <p className="mt-4">Selectați o culoare pentru a evidenția aceasta și culorile sale complementare împărțite. Restul vor deveni gri.</p>
        </div>
    );
}

export default RoataCulorilorSplitComplementary;
