import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Nuj from './Teorie/teorie';
import Joc from './Joc/joc';
import Settings from './Setari/setari';
import Acasa from './Acasa/acasa';
import Laborator from './Laborator/laborator';
import Quiz from './Quizz-uri/quiz';
import ConditionalNav from './Navigatie/navigareConditionata'; // Asigură-te că această cale este corectă
import './index.css';
import ButtonsPage from './Teorie/buttonPage';
import TheoryPage from './Teorie/teorie'

import Introducere from './Teorie/Introducere';
import Perceptia from './Teorie/Perceptia'
import Armonia from './Teorie/ArmoniaCulorilor'
import ContrastulSiBalanta from './Teorie/ContrastulInArta';
import Sistemele from './Teorie/Sistemele'
function App() {
  return (
    <>

    <Router>

      <ConditionalNav />
      <div className="App">
        <Routes>
          <Route path="/" element={<Acasa />} />
          <Route path="/joc" element={<Joc />} />
          <Route path="/teorie" element={<Nuj />} />
          <Route path="/laborator" element={<Laborator />} />
          <Route path="/setari" element={<Settings />} />
          <Route path="/quiz/*" element={<Quiz />} />
          <Route path="/teorie" element={<ButtonsPage />} />
        <Route path="/teorie/:buttonNumber" element={<TheoryPage />} />
        <Route path="/teorie/introducere" element={<Introducere />} />
        <Route path="/teorie/perceptia" element={<Perceptia />} />
        <Route path="/teorie/armonia" element={<Armonia />} />
        <Route path="/teorie/constrastulsibalanta" element={<ContrastulSiBalanta />} />
        <Route path="/teorie/sistemele" element={<Sistemele />} />
        </Routes>
      </div>
    </Router>
    </>
  );
}

export default App;
