import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link, Routes, useParams } from 'react-router-dom';
import './quiz.css';

import Istoria from './Istoria.json';
import Perceptia from './Perceptia.json';
import Contrastul_in_Web_Design_si_Tehnologie from './ContrastulWeb.json';
import Contrastul_in_arta from './ContrastulArta.json';
import Durabilitatea from './Durabilitatea.json';
import Lectie_1 from './1.json';
import Lectie_2 from './2.json';
import Lectie_3 from './3.json';
import Lectie_4 from './4.json';

function QuizDetail() {
    const { quizName } = useParams();
    const quizzes = {
        istoria: Istoria,
        perceptia: Perceptia,
        contrastul_in_web_design_si_tehnologie: Contrastul_in_Web_Design_si_Tehnologie,
        contrastul_in_arta: Contrastul_in_arta,
        durabilitatea: Durabilitatea,
        lectie_1: Lectie_1,
        lectie_2: Lectie_2,
        lectie_3: Lectie_3,
        lectie_4: Lectie_4
    };
    
    const quizData = quizzes[quizName];

    return (
        <div>
            <h1>{quizData.title}</h1>
            {quizData.questions.map((question, idx) => (
                <div key={idx}>
                    <p>{question.question}</p>
                    {question.options.map((option, index) => (
                        <button key={index} className="btn1">
                            {option}
                        </button>
                    ))}
                </div>
            ))}
        </div>
    );
}

export default function Quiz() {
    return (
        <Router>
            <div className="container w-full mx-auto p-4">
                <div className="flex flex-wrap justify-center items-center gap-4 w-full">
                    {Object.keys(Istoria).map((quizName, index) => (
                        <Link to={`/quizz-uri/${quizName.toLowerCase()}`} key={index} className="w-1/4 flex justify-center">
                            <button className="w-full bg-gray-600 px-5 py-3 h-16 text-2xl rounded-lg text-white hover:bg-gray-800 duration-500">
                                {quizName.replace(/_/g, ' ')}
                            </button>
                        </Link>
                    ))}
                </div>
                
                <Routes>
                    <Route path="/quizz-uri/:quizName" element={<QuizDetail />} />
                </Routes>
            </div>
        </Router>
    );
}
