import React, { useEffect, useState } from 'react';
import './animatii.css'
function BubbleEffect() {
  const [bubbles, setBubbles] = useState([]);

  useEffect(() => {
    const interval = setInterval(() => {
      const size = Math.random() * (120 - 40) + 40; // Bule cu dimensiuni între 40px și 120px
      const duration = Math.random() * (15 - 5) + 5; // Durata animației între 5 și 15 secunde
      const left = Math.random() * 100; // Poziționare aleatoare pe orizontală

      const bubble = {
        id: bubbles.length,
        size,
        duration,
        left,
      };

      setBubbles(currentBubbles => [...currentBubbles, bubble]);

      // Limităm numărul de bule la 20 pentru a evita supraîncărcarea DOM-ului
      if (bubbles.length > 20) {
        setBubbles(currentBubbles => currentBubbles.slice(1));
      }
    }, 2000);

    return () => clearInterval(interval);
  }, [bubbles]);

  return (
    <div className="relative w-full h-4 overflow-hidden">
      {bubbles.map(bubble => (
        <div
          key={bubble.id}
          className="absolute bg-blue-500 rounded-full opacity-60"
          style={{
            width: bubble.size,
            height: bubble.size,
            animation: `rise ${bubble.duration}s linear infinite`,
            left: `${bubble.left}%`,
            bottom: `-150px`, // Start off-screen
          }}
        />
      ))}
    </div>
  );
}

export default BubbleEffect;
