import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import './quiz.css'

import Istoria from './Istoria.json';
import Perceptia from './Perceptia.json';
import Contrastul_in_Web_Design_si_Tehnologie from './ContrastulWeb.json';
import Contrastul_in_arta from './ContrastulArta.json';
import Durabilitatea from './Durabilitatea.json';
import Lectie_1 from './1.json';
import Lectie_2 from './2.json';
import Lectie_3 from './3.json';
import Lectie_4 from './4.json';

export default function Quiz() {
    const [currentQuizData, setCurrentQuizData] = useState(null);

    const quizzes = {
        Istoria,
        Perceptia,
        Contrastul_in_Web_Design_si_Tehnologie,
        Contrastul_in_arta,
        Durabilitatea,
        Lectie_1,
        Lectie_2,
        Lectie_3,
        Lectie_4
    };

    const handleQuizSelection = (quizName) => {
        setCurrentQuizData(quizzes[quizName]);
    };

    return (
     
        <div className="container w-full mx-auto p-4">
        <div className="flex flex-wrap justify-center items-center gap-4 w-full">
            {Object.keys(quizzes).map((quizName, index) => (
                <Link to={`/quizz-uri/${quizName.replace(/\s/g, '_').toLowerCase()}`} key={index}
                    className="w-1/4 flex justify-center" // Ajustat pentru a permite 4 coloane
                >
                    <button
                        className="w-full bg-gray-600 px-5 py-3 h-16 text-2xl rounded-lg text-white hover:bg-gray-800 duration-500 lg:text-3xl sm:text-xl md:text-xs;"
                    >
                        {quizName.replace(/_/g, ' ')}
                    </button>
                </Link>
            ))}
        </div>
        
                
                <Routes>
                    {Object.keys(quizzes).map((quizName, index) => (
                        <Route path={`/quizz-uri/${quizName.replace(/\s/g, '_').toLowerCase()}`} key={index} render={() => (
                            <div>
                                <h1>{quizzes[quizName].title}</h1>
                                {quizzes[quizName].questions.map((question, idx) => (
                                    <div key={idx}>
                                        <p>{question.question}</p>
                                        {question.options.map((option, index) => (
                                            <button key={index} className="btn1">
                                                {option}
                                            </button>
                                        ))}
                                    </div>
                                ))}
                            </div>
                        )} />
                    ))}
                </Routes>
            </div>
      
    );
}
