import React, { useState, useEffect } from 'react';
import ButtonsComponent from '../buttonComponents';
function DurabilitateaCulorilor() {
    const [content, setContent] = useState([]);

    useEffect(() => {
        // Simulare încărcare conținut dintr-o sursă externă (e.g., API, fișier local)
        const loadedContent = [
            { id: 1, title: "Introducere în Durabilitatea Culorilor", text: "Explorăm conceptul de durabilitate a culorilor în diferite medii și aplicații." },
            { id: 2, title: "Stabilitatea Culoarei", text: "Cum anumiți pigmenți își păstrează intensitatea și luminozitatea în timp sub expunerea la lumina solară." },
            { id: 3, title: "Impactul Mediului", text: "Analizăm cum factorii de mediu afectează durabilitatea culorilor în artă și industrie." },
            { id: 4, title: "Tehnologii de Protecție", text: "Discutăm despre tehnologiile utilizate pentru a proteja culorile împotriva decolorării și deteriorării." },
            { id: 5, title: "Culorile în Arta Outdoor", text: "Specificități privind selectarea pigmenților pentru lucrările expuse la exterior." },
            { id: 6, title: "Durabilitatea în Designul de Interior", text: "Importanța alegerii materialelor durabile pentru designul interior." },
            { id: 7, title: "Testarea Durabilității Culorilor", text: "Metode standardizate de testare a rezistenței culorilor în diferite condiții." },
            { id: 8, title: "Culori Sustenabile", text: "Explorarea opțiunilor de pigmenți ecologici și impactul lor asupra mediului." },
            { id: 9, title: "Inovații în Pigmenți", text: "Noile descoperiri în chimia culorilor care îmbunătățesc durabilitatea." },
            { id: 10, title: "Rolul UV în Decolorare", text: "Cum radiațiile ultraviolete contribuie la degradarea rapidă a culorilor." },
            { id: 11, title: "Culori pentru Textile", text: "Particularități ale durabilității culorilor în industria textilă." },
            { id: 12, title: "Impactul Temperaturii", text: "Influența temperaturii asupra stabilității culorilor în diverse materiale." },
            { id: 13, title: "Culori în Publicitate", text: "Importanța culorilor durabile în materialele publicitare expuse elementelor." },
            { id: 14, title: "Culori în Automotive", text: "Considerații ale durabilității culorilor în industria auto." },
            { id: 15, title: "Culorile în Artă", text: "Cum alegerea corectă a pigmenților influențează longevitatea operelor de artă." },
            { id: 16, title: "Prevenirea Decolorării", text: "Strategii eficiente pentru menținerea vivacității culorilor în timp." },
            { id: 17, title: "Materiale Reflectorizante", text: "Utilizarea materialelor reflectorizante pentru a prelungi durata de viață a culorilor." },
            { id: 18, title: "Culori pentru Jucării", text: "Considerații de siguranță și durabilitate pentru culorile utilizate în jucării." },
            { id: 19, title: "Reglementări și Standarde", text: "Cum reglementările internaționale influențează alegerea și utilizarea pigmenților durabili." },
            { id: 20, title: "Concluzie", text: "Sumarizare a importanței durabilității culorilor în design și producție." }
        ];
        setContent(loadedContent);
    }, []);

    return (
        
        <div class="flex h-auto">
        <div className="w-1/6 bg-gray-600 p-4 overflow-auto">
               <ButtonsComponent currentButton={6} />
           </div>
           <div className="w-5/6 p-4 flex flex-col bg-gray-100">
            <h1>Durabilitatea Culorilor</h1>
            {content.map(paragraph => (
                <div key={paragraph.id}>
                    <h2>{paragraph.title}</h2>
                    <p>{paragraph.text}</p>
                </div>
            ))}
        </div>
        </div>

    );
}

export default DurabilitateaCulorilor;
