import React, { useEffect, useState } from 'react';
import './animatii.css';

function BubbleEffect() {
  const [bubble, setBubble] = useState(null);

  useEffect(() => {
    const createBubble = () => {
      const size = Math.random() * (80 - 20) + 20;  // Mărimea bulei între 20px și 80px
      const duration = Math.random() * (15 - 5) + 5;  // Durata între 5 și 15 secunde
      const left = Math.random() * 100;  // Poziție aleatorie pe axa orizontală între 0% și 100%

      const newBubble = {
        id: new Date().getTime(),  // ID unic bazat pe timestamp
        size,
        duration,
        left,
      };

      setBubble(newBubble);  // Setăm noua bulă ca stare curentă

      // Planificăm crearea unei noi bule după ce aceasta termină traseul
      setTimeout(createBubble, (duration + 5) * 1000);  // +5 secunde întârziere suplimentară
    };

    createBubble();  // Inițiem prima bulă la încărcarea componentei

    // Curățăm timeout-ul la demontarea componentei
    return () => clearTimeout(createBubble);
  }, []);

  return (
    <div className="relative w-full h-full overflow-hidden">
      {bubble && (
        <div
          key={bubble.id}
          className="absolute bg-blue-500 rounded-full opacity-60"
          style={{
            width: `${bubble.size}px`,
            height: `${bubble.size}px`,
            left: `${bubble.left}%`,
            bottom: `-150px`,  // Start off-screen
            animation: `rise ${bubble.duration}s linear infinite`
          }}
        />
      )}
    </div>
  );
}

export default BubbleEffect;
