import React, { useState, useEffect } from 'react';

function RoataCulorilorAnaloge() {
    const [selectedSegment, setSelectedSegment] = useState(null);
    const segments = 12; // Definim numărul total de segmente

    useEffect(() => {
        // Selectează un segment aleatoriu la încărcarea componentei
        setSelectedSegment(Math.floor(Math.random() * segments));
    }, [segments]);

    const colors = [
        'hsl(240, 80%, 50%)', 'hsl(210, 80%, 50%)', 'hsl(180, 80%, 50%)',
        'hsl(150, 80%, 50%)', 'hsl(120, 80%, 50%)', 'hsl(90, 80%, 50%)',
        'hsl(75, 80%, 50%)', 'hsl(45, 80%, 50%)', 'hsl(30, 80%, 50%)',
        'hsl(0, 80%, 50%)', 'hsl(330, 80%, 50%)', 'hsl(300, 80%, 50%)'
    ];

    const getCoordinatesForPercent = (percent) => {
        const x = Math.cos(2 * Math.PI * percent);
        const y = Math.sin(2 * Math.PI * percent);
        return [x, y];
    };

    const getPathData = (index, segments) => {
        const startPercent = index / segments;
        const endPercent = (index + 1) / segments;
        const [startX, startY] = getCoordinatesForPercent(startPercent);
        const [endX, endY] = getCoordinatesForPercent(endPercent);

        return `M 0 0 L ${startX * 16} ${startY * 16} A 16 16 0 0 1 ${endX * 16} ${endY * 16} z`;
    };

    const adjustColor = (color, index) => {
        if (selectedSegment === index) {
            return color;
        } else {
            return 'grey'; // Setează culoarea pentru segmentele neselectate ca gri
        }
    };

    return (
        <div className="text-center">
            <h1 className="text-2xl font-bold my-4">Roata Culorilor Analoage</h1>
            <svg viewBox="-18 -18 36 36" width="300px" height="300px">
                {colors.map((color, index) => (
                    <path
                        key={index}
                        d={getPathData(index, segments)}
                        fill={adjustColor(color, index)}
                        onClick={() => setSelectedSegment(index)}
                        style={{ cursor: 'pointer' }}
                    />
                ))}
            </svg>
            <p className="mt-4">Selectați un segment pentru a evidenția variația de culoare, iar restul segmentelor vor deveni gri.</p>
        </div>
    );
}

export default RoataCulorilorAnaloge;
