import React, { useState, useEffect } from 'react';
import { SketchPicker } from 'react-color';

function CreatorPaleta() {
    const [culori, setCulori] = useState([]);
    const [culoareSelectata, setCuloareSelectata] = useState('#ffffff');

    // Încărcarea paletei salvate din localStorage când componenta este montată
    useEffect(() => {
        const paletaSalvata = JSON.parse(localStorage.getItem('paletaCulori'));
        if (paletaSalvata && paletaSalvata.length > 0) {
            setCulori(paletaSalvata);
        }
    }, []);

    const schimbaCuloare = (culoare) => {
        setCuloareSelectata(culoare.hex);
    };

    const adaugaCuloare = () => {
        if (!culori.includes(culoareSelectata)) {
            setCulori(culoriPrev => [...culoriPrev, culoareSelectata]);
        }
    };

    const eliminaCuloare = (culoareDeEliminat) => {
        setCulori(culoriPrev => culoriPrev.filter(culoare => culoare !== culoareDeEliminat));
    };

    const salveazaPaleta = () => {
        localStorage.setItem('paletaCulori', JSON.stringify(culori));
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4">
            <SketchPicker color={culoareSelectata} onChangeComplete={schimbaCuloare} />
            <button className="mt-4 bg-blue-500 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" onClick={adaugaCuloare}>
                Adaugă Culoare
            </button>
            <button className="mt-2 bg-green-500 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline" onClick={salveazaPaleta}>
                Salvează Paleta
            </button>
            {culori.length > 0 && (
                <div className="flex flex-wrap justify-center mt-4 w-full">
                    {culori.map((culoare, index) => (
                        <div key={index} className="m-2 w-16 h-16 flex justify-center items-center relative" style={{ backgroundColor: culoare }}>
                            <button className="absolute inset-0 w-full h-full opacity-0" onClick={() => eliminaCuloare(culoare)}>Elimină</button>
                            <span className="text-white text-sm">X</span>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default CreatorPaleta;
