import React, { useState } from 'react';

function RoataCulorilorSplitComplementary() {
    const [selectedSegment, setSelectedSegment] = useState(null);

    const colors = [
        'hsl(240, 80%, 50%)', // Albastru
        'hsl(45, 80%, 50%)', // Galben-portocaliu
        'hsl(75, 80%, 50%)', // Galben-verde

        'hsl(0, 80%, 50%)', // Roșu
        'hsl(165, 80%, 50%)', // Albastru-verde
        'hsl(270, 80%, 50%)', // Albastru-violet

        'hsl(120, 80%, 50%)', // Verde
        'hsl(330, 80%, 50%)', // Roșu-violet
        'hsl(0, 80%, 50%)', // Roșu

        'hsl(60, 80%, 50%)', // Galben
        'hsl(285, 80%, 50%)', // Violet
        'hsl(255, 80%, 50%)' // Albastru-violet
    ];

    return (
        <div className="text-center">
            <h1 className="text-2xl font-bold my-4">Roata Culorilor Complementare Împărțite</h1>
            <svg viewBox="-18 -18 36 36" width="300px" height="300px">
                {colors.map((color, index) => (
                    <circle key={index} cx={0} cy={0} r={16} fill={color} transform={`rotate(${index * 30}) translate(0, -16)`} />
                ))}
            </svg>
            <p className="mt-4">Selectați o secțiune pentru a vedea exemple de culori complementare „împărțite”.</p>
        </div>
    );
}

export default RoataCulorilorSplitComplementary;
