import  { useState } from 'react';
import '../stiluri.css'
function MixerCuloareRGBA() {
    const [rosu, setRosu] = useState(0);
    const [verde, setVerde] = useState(0);
    const [albastru, setAlbastru] = useState(0);
    const [alfa, setAlfa] = useState(1);

    const handleRosuChange = (e) => setRosu(e.target.value);
    const handleVerdeChange = (e) => setVerde(e.target.value);
    const handleAlbastruChange = (e) => setAlbastru(e.target.value);
    const handleAlfaChange = (e) => setAlfa(e.target.value);

    return (
        <div className="flex">
            <h2 className="w-1/6">Mixer de Culori RGBA</h2>
            <div className="w-3/5">
            <div>
                <label>Rosu (0-255): </label>
                <input type="range" min="0" max="255" value={rosu} onChange={handleRosuChange} />
                {rosu}
            </div>
            <div>
                <label>Verde (0-255): </label>
                <input type="range" min="0" max="255" value={verde} onChange={handleVerdeChange} />
                {verde}
            </div>
            <div>
                <label>Albastru (0-255): </label>
                <input type="range" min="0" max="255" value={albastru} onChange={handleAlbastruChange} />
                {albastru}
            </div>
            <div>
                <label>Alfa (0-1): </label>
                <input type="range" min="0" max="1" step="0.01" value={alfa} onChange={handleAlfaChange} />
                {alfa}
            </div>
            </div>
            <div style={{ margin:'auto', width: '100px', height: '100px',  backgroundColor: `rgba(${rosu}, ${verde}, ${albastru}, ${alfa})`,  display: 'flex', alignItems: 'center', justifyContent: 'center', color: 'white', textAlign: 'center' }}>
        Culoarea RezultatÄƒ
    </div>
        </div>
    );
}

export default MixerCuloareRGBA;
