import React, { useState } from 'react';

const intrebari = [
  {
    intrebare: "Ce culoare evocă pasiune și energie?",
    optiuni: ["Verde", "Roșu", "Albastru", "Gri"],
    raspunsCorect: "Roșu"
  },
  {
    intrebare: "Care culoare este asociată cu relaxarea și este odihnitoare pentru ochi?",
    optiuni: ["Galben", "Mov", "Verde", "Portocaliu"],
    raspunsCorect: "Verde"
  },
  {
    intrebare: "Care culoare stimulează apetitul în restaurante?",
    optiuni: ["Portocaliu", "Albastru", "Negru", "Alb"],
    raspunsCorect: "Portocaliu"
  },
  
];

function QuizPerceptiaCulorilor() {
    const [intrebareCurenta, setIntrebareCurenta] = useState(0);
    const [optiuniSelectate, setOptiuniSelectate] = useState([]);
    const [arataRezultate, setArataRezultate] = useState(false);

    const selecteazaOptiune = (optiune) => {
        const newOptiuniSelectate = [...optiuniSelectate];
        newOptiuniSelectate[intrebareCurenta] = optiune;
        setOptiuniSelectate(newOptiuniSelectate);

        const esteUltimaIntrebare = intrebareCurenta === intrebari.length - 1;
        if (esteUltimaIntrebare) {
            setArataRezultate(true);
        } else {
            setIntrebareCurenta(intrebareCurenta + 1);
        }
    };
    const calculeazaScor = () => {
        return intrebari.reduce((scor, intrebare, index) => {
            if (intrebare.raspunsCorect === optiuniSelectate[index]) {
                return scor + 1;
            }
            return scor;
        }, 0);
    };
    const restartQuiz = () => {
        setIntrebareCurenta(0);
        setOptiuniSelectate([]);
        setArataRezultate(false);
    };

    return (
        <div className="container mx-auto p-4">
            {arataRezultate ? (
                <div>
                    <h1>Rezultatele Quizului</h1>
                    <p>Ai obținut {optiuniSelectate.filter((opt, index) => intrebari[index].raspunsCorect === opt).length} din {intrebari.length} puncte.</p>
                    <div>
                        {intrebari.map((intrebare, index) => {
                            const raspunsCorect = intrebare.raspunsCorect;
                            const raspunsUtilizator = optiuniSelectate[index];
                            return raspunsUtilizator !== raspunsCorect ? (
                                <div key={index}>
                                    <p>{intrebare.intrebare}</p>
                                    <p>Răspunsul tău: {raspunsUtilizator}</p>
                                    <p>Răspuns corect: {raspunsCorect}</p>
                                </div>
                            ) : null;
                        })}
                    </div>
                    <button onClick={restartQuiz} className="m-auto p-2 bg-blue-500 text-white rounded hover:bg-blue-700">
                        Restart Quiz
                    </button>
                </div>
            )  : (
                <div>
                    <h2 className='text-center'>{intrebari[intrebareCurenta].intrebare}</h2>
                    <div>
                        {intrebari[intrebareCurenta].optiuni.map((optiune, index) => (
                            <button key={index} onClick={() => selecteazaOptiune(optiune)} className='w-full my-2 text-blue-900 hover:bg-gray-600 hover:text-white duration-500'>
                                {optiune}

                            </button>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
}

export default QuizPerceptiaCulorilor;
