import React from 'react';
import './quiz.css';

export default function Quiz() {
    const quizNames = [
        'Introducere',
        'Istoria',
        'Percepția',
        'Contrastul in Web Design si Tehnologie',
        'Contrastul in arta',
        'Durabilitatea',
        'Lecție 5',
        'Lecție 6',
        'Lecție 7',
        'Lecție 8'
    ];

    const handleQuizSelection = (quizName) => {
        // Aici poți adăuga logica pentru încărcarea datelor quizului
        // de exemplu: loadQuizData(`/quizz-uri/${quizName}.json`);
        console.log(`Încărcare quiz: ${quizName}`);
    };

    return (
        <div className="flex h-auto bg-gray-400 p-4 pb-10">
            <div className="w-full flex xl:columns-3 flex-wrap gap-8 justify-center items-center lg:columns-2 sm:columns-1">
                {quizNames.map((name, index) => (
                    <button
                        key={index}
                        className="btn1"
                        onClick={() => handleQuizSelection(name.replace(/\s/g, '_'))} // înlocuiește spațiile cu underscore pentru a forma calea corectă
                    >
                        {name}
                    </button>
                ))}
            </div>
        </div>
    );
}
