import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Nuj from './Teorie/teorie';
import Joc from './Joc/joc';
import Settings from './Setari/setari';
import Acasa from './Acasa/acasa';
import Laborator from './Laborator/laborator';
import Quiz from './Quizz-uri/quiz';
import ConditionalNav from './Navigatie/navigareConditionata'; // Verifică dacă calea este corectă
import './index.css';

// Import Particule if needed
// import Particule from './Particule'

function App() {
  return (
    <>
      <Router>
        <ConditionalNav />  // Navigația condiționată se afișează indiferent de ruta accesată
        <div className="App">
          <Routes>
            <Route path="/" element={<Acasa />} />  // Pagina de start
            <Route path="/joc" element={<Joc />} />  // Pagina pentru joc
            <Route path="/teorie" element={<Nuj />} />  // Pagina de teorie
            <Route path="/laborator" element={<Laborator />} />  // Pagina pentru laborator
            <Route path="/setari" element={<Settings />} />  // Pagina de setări
            <Route path="/quiz/*" element={<Quiz />} />  // Pagina pentru quiz-uri, folosind un wildcard pentru sub-rute
          </Routes>
        </div>
      </Router>
    </>
  );
}

export default App;
