// ButtonsComponent.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';

function ButtonsComponent({ currentButton }) {
    const navigate = useNavigate();

    const handleButtonClick = (buttonNumber) => {
        navigate(`/teorie/${buttonNumber}`);
    };

    return (
        <div className="flex flex-col items-center justify-center border text-center rounded mt-10" style={{ width: '15%' }}>
            {Array.from({ length: 20 }, (_, index) => (
                <button
                    key={index + 1}
                    onClick={() => handleButtonClick(index + 1)}
                    className={`w-full my-2 px-4 py-1 rounded bg-blue-500 text-white hover:bg-blue-700 focus:outline-none ${currentButton === index + 1 ? 'bg-blue-700' : ''}`}
                >
                    {index + 1 === 1 ? 'Introducere' : `Buton ${index + 1}`}
                </button>
            ))}
        </div>
    );
}

export default ButtonsComponent;
