import React, { useState } from 'react';

function RoataCulorilorComplementareImpartite() {
    const [segmentSelectat, setSegmentSelectat] = useState(null);

    const culori = [
        { principal: 'hsl(30, 80%, 50%)', split1: 'hsl(60, 80%, 50%)', split2: 'hsl(0, 80%, 50%)' },
        { principal: 'hsl(150, 80%, 50%)', split1: 'hsl(180, 80%, 50%)', split2: 'hsl(120, 80%, 50%)' },
        { principal: 'hsl(270, 80%, 50%)', split1: 'hsl(300, 80%, 50%)', split2: 'hsl(240, 80%, 50%)' },
        { principal: 'hsl(90, 80%, 50%)', split1: 'hsl(120, 80%, 50%)', split2: 'hsl(60, 80%, 50%)' },
    ];

    const calculeazaCoordonate = (procent) => {
        const x = Math.cos(2 * Math.PI * procent);
        const y = Math.sin(2 * Math.PI * procent);
        return [x, y];
    };

    const getPathData = (index, segments) => {
        const startPercent = index / segments;
        const endPercent = (index + 1) / segments;
        const [startX, startY] = calculeazaCoordonate(startPercent);
        const [endX, endY] = calculeazaCoordonate(endPercent);

        return `M 0 0 L ${startX * 16} ${startY * 16} A 16 16 0 0 1 ${endX * 16} ${endY * 16} z`;
    };

    const ajusteazaCuloare = (index) => {
        if (segmentSelectat !== null) {
            if (index === segmentSelectat || index === (segmentSelectat + 1) % 4 || index === (segmentSelectat + 2) % 4) {
                return culori[Math.floor(segmentSelectat / 3)].principal;
            }
            return 'grey';
        }
        return culori[index].principal;
    };

    return (
        <div className="text-center">
            <h1 className="text-2xl font-bold my-4">Roata Culorilor Complementare Împărțite</h1>
            <svg viewBox="-18 -18 36 36" width="300px" height="300px">
                {culori.map((_, index) => (
                    <path
                        key={index}
                        d={getPathData(index, 12)}
                        fill={ajusteazaCuloare(index)}
                        onClick={() => setSegmentSelectat(index)}
                        style={{ cursor: 'pointer' }}
                    />
                ))}
            </svg>
            <p className="mt-4">Selectați o culoare pentru a evidenția aceasta și cele două culori complementare împărțite. Restul vor deveni gri.</p>
        </div>
    );
}

export default RoataCulorilorComplementareImpartite;
