// ButtonsComponent.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';

function ButtonsComponent({ currentButton }) {
    const navigate = useNavigate();

    const handleButtonClick = (buttonNumber) => {
        navigate(`/teorie/${buttonNumber}`);
    };

    return (

        <div className="flex flex-col items-center justify-center w-full">dddddddddddddd
            {Array.from({ length: 20 }, (_, index) => (
                <button
                    key={index + 1}
                    onClick={() => handleButtonClick(index + 1)}
                    className={`w-full my-1 py-2 text-sm font-medium text-white rounded transition-colors duration-200
                                ${currentButton === index + 1 ? 'bg-blue-700' : 'bg-blue-500 hover:bg-blue-600'} 
                                focus:outline-none focus:ring-2 focus:ring-blue-700 focus:ring-opacity-50`}
                >
                    {index + 1 === 1 ? 'Introducere' : `Teorie ${index + 1}`}
                </button>
            ))}
        </div>
    );
}

export default ButtonsComponent;
