import React, { useState, useEffect } from 'react';

function RoataMonocromaticaVariata() {
    const [selectedSegment, setSelectedSegment] = useState(null);
    const segments = 4;  // Definim 4 segmente principale
    const subSegments = 3;  // Fiecare segment principal va avea 3 sub-segmente
    const colors = [
        // Nuanțe de roșu
        'hsl(0, 90%, 60%)',  // Roșu deschis
        'hsl(0, 90%, 50%)',  // Roșu mediu
        'hsl(0, 90%, 40%)',  // Roșu închis
    
        // Nuanțe de albastru
        'hsl(240, 90%, 60%)',  // Albastru deschis
        'hsl(240, 90%, 50%)',  // Albastru mediu
        'hsl(240, 90%, 40%)',  // Albastru închis
    
        // Nuanțe de verde
        'hsl(120, 60%, 60%)',  // Verde deschis
        'hsl(120, 60%, 50%)',  // Verde mediu
        'hsl(120, 60%, 40%)',  // Verde închis
    
        // Nuanțe de mov
        'hsl(300, 60%, 70%)',  // Mov deschis
        'hsl(300, 60%, 60%)',  // Mov mediu
        'hsl(300, 60%, 50%)'   // Mov închis
    ];
    
    useEffect(() => {
        // Selectăm un segment aleatoriu
        setSelectedSegment(Math.floor(Math.random() * segments));
    }, []);

    const getCoordinatesForPercent = (percent) => {
        const x = Math.cos(2 * Math.PI * percent);
        const y = Math.sin(2 * Math.PI * percent);
        return [x, y];
    };

    const getPathData = (index, subIndex) => {
        const totalSubSegments = segments * subSegments;
        const startPercent = (index * subSegments + subIndex) / totalSubSegments;
        const endPercent = (index * subSegments + subIndex + 1) / totalSubSegments;
        const [startX, startY] = getCoordinatesForPercent(startPercent);
        const [endX, endY] = getCoordinatesForPercent(endPercent);

        return `M 0 0 L ${startX * 16} ${startY * 16} A 16 16 0 0 1 ${endX * 16} ${endY * 16} z`;
    };

    const adjustColor = (color, index) => {
        if (selectedSegment === index) {
            // Returnează culoarea normală dacă este segmentul selectat
            return color;
        } else {
            // Returnează gri pentru neselectate
            return 'grey';
        }
    };

    return (
        <div className="text-center">
            <h1 className="text-2xl font-bold my-4">Roata Monocromatică Variată</h1>
            <svg viewBox="-18 -18 36 36" width="300px" height="300px">
                {Array.from({ length: segments }).map((_, index) => (
                    Array.from({ length: subSegments }).map((_, subIndex) => (
                        <path
                            key={`${index}-${subIndex}`}
                            d={getPathData(index, subIndex)}
                            fill={adjustColor(colors[index * subSegments + subIndex], index)}
                            onClick={() => setSelectedSegment(index)}
                            style={{ cursor: 'pointer' }}
                        />
                    ))
                ))}
            </svg>
            <p className="mt-4">Selectați un segment pentru a vedea nuanțele diferite ale aceleiași culori. Restul segmentelor vor deveni gri.</p>
        </div>
    );
}

export default RoataMonocromaticaVariata;
