import React, { useState } from 'react';

function QuizDetails({ quizData }) {
    const [selectedAnswers, setSelectedAnswers] = useState({});

    // Functia care se apelează la click pe un răspuns
    const handleOptionClick = (questionIndex, option) => {
        // Setăm răspunsul selectat pentru întrebarea curentă
        setSelectedAnswers({
            ...selectedAnswers,
            [questionIndex]: option
        });
    };

    // Verificăm dacă răspunsul selectat este corect
    const isAnswerCorrect = (questionIndex) => {
        return quizData[questionIndex].raspunsCorect === selectedAnswers[questionIndex];
    };

    return (
        <div>
            <h1>Quiz {quizData}</h1>
            {quizData.map((question, idx) => (
                <div key={idx} className="question-container">
                    <p>{question.intrebare}</p>
                    <div className="options-container">
                        {question.optiuni.map((option, index) => (
                            <button 
                                key={index}
                                onClick={() => handleOptionClick(idx, option)}
                                className={`option ${selectedAnswers[idx] === option ? "selected" : ""}`}
                            >
                                {option}
                            </button>
                        ))}
                    </div>
                    {selectedAnswers[idx] !== undefined && (
                        <p className={`answer-feedback ${isAnswerCorrect(idx) ? "correct" : "incorrect"}`}>
                            {isAnswerCorrect(idx) ? "Răspuns corect!" : "Răspuns greșit!"}
                        </p>
                    )}
                </div>
            ))}
        </div>
    );
}

export default QuizDetails;
