import React, { useState, useEffect } from 'react';

function RoataMonocromaticaVariata() {
    const [selectedSegment, setSelectedSegment] = useState(null);
    const segments = 4; // Numărul total de segmente
    const colors = [
        '#E63946', '#F4A261', '#E9C46A', '#2A9D8F',
        '#264653', '#2B2D42', '#8D99AE', '#EDF2F4',
        '#EF233C', '#D90429', '#06D6A0', '#118AB2'
    ];

    useEffect(() => {
        // La încărcarea paginii, selectăm un segment aleatoriu
        setSelectedSegment(Math.floor(Math.random() * segments));
    }, []);

    const getCoordinatesForPercent = (percent) => {
        const x = Math.cos(2 * Math.PI * percent);
        const y = Math.sin(2 * Math.PI * percent);
        return [x, y];
    };

    const getPathData = (index, part) => {
        const startPercent = (index + part * 0.33) / segments;
        const endPercent = (index + part * 0.33 + 0.33) / segments;
        const [startX, startY] = getCoordinatesForPercent(startPercent);
        const [endX, endY] = getCoordinatesForPercent(endPercent);

        return `M 0 0 L ${startX * 16} ${startY * 16} A 16 16 0 0 1 ${endX * 16} ${endY * 16} z`;
    };

    const adjustColor = (color, part) => {
        let lightness = 40 + part * 20; // Ajustează luminozitatea pentru fiecare sub-segment
        return `hsl(${(color.match(/\d+/g).reduce((a, b) => a + parseInt(b), 0) % 360)}, 100%, ${lightness}%)`;
    };

    return (
        <div className="text-center">
            <h1 className="text-2xl font-bold my-4">Roata Monocromatică Variată</h1>
            <svg viewBox="-18 -18 36 36" width="300px" height="300px">
                {colors.map((color, index) => (
                    [0, 1, 2].map(part => (
                        <path
                            key={`${index}-${part}`}
                            d={getPathData(index, part)}
                            fill={adjustColor(color, part)}
                            onClick={() => setSelectedSegment(index)}
                            style={{ cursor: 'pointer', opacity: selectedSegment === index ? 1 : 0.3 }}
                        />
                    ))
                ))}
            </svg>
            <p className="mt-4">Selectați un segment pentru a vedea nuanțele diferite ale aceleiași culori.</p>
        </div>
    );
}

export default RoataMonocromaticaVariata;
